        <div class="timers">
            <ul class="timers-list">
                <li class="timer-item"
                    v-for="item in timers"
                    :class="{active:timeState == item.times}"
                    @click="activeTime(item.times)">
                    <p>{{item.times}}</p>
                    <p>{{item.status | getStatus}}</p>
                </li>
            </ul>
            <div class="spike-list">
                <ul>
                    <li v-for="item in spikeList">
                        <img :src="item.imgUrl" alt="">
                        <div class="desciption">
                            <p class="details">
                                {{item.name}}
                            </p>
                            <p>
                                <button class="button enter-btn" @click="$router.push({ name: 'spikeList', params: { typeId: item.id }})">马上进入</button>
                            </p>
                        </div>
                    </li>
                    <li class="normal">
                        <div class="more">
                            <p>更多精彩</p>
                            <p>敬请期待</p>
                        </div>
                    </li>
                </ul>
            </div>
        </div>
    </div>
</template>
