<script type="text/ecmascript-6">
    import request from  '../../shared/request/asyncRequest'
    export default {
        data(){
            return {
                miao: false,
                sha: false,
                lai: false,
                xi: false,
                timers: [],
                timeState: '',
                spikeList: []

            }
        },
        methods: {
            activeTime(item){
                this.timeState = item
                request.spike.getSpikeList({time: item}).then(data => {
                    this.spikeList = data.rows
                })
            }
        },
        filters: {
            getStatus: function (value) {
                return value == 0 ? '已结束' : '进行中'
            }
        },
        mounted(){
            var self = this;
            setTimeout(function () {
                self.miao = true
            }, 50);
            setTimeout(function () {
                self.sha = true
            }, 100);
            setTimeout(function () {
                self.lai = true
            }, 150);
            setTimeout(function () {
                self.xi = true
            }, 200);
            request.spike.getSpikeTime({}).then(data => {
                this.timers = data.rows;
                this.timeState = data.rows[1].times;
                this.activeTime(this.timeState)
            })
        }
    }
</script>